project.select_master:
SELECT IPA1.COD, IPA1.X_UTM, IPA1.Y_UTM, IPA1.TOPONIMIA, IPA1.ACUIFERO
FROM IPA1
WHERE COD='';

project.select_temporal:
SELECT IPA2.COD, IPA2.FECHA, IPA2.SITUACION, IPA2.PNP, [ipa1].[z]-[pnp] AS CNP
FROM IPA1 INNER JOIN IPA2 ON IPA1.COD = IPA2.COD
WHERE IPA2.COD=?;

project.select_horizontal_lines:
SELECT IPA1_LINEAS_HORIZONTALES.COD, IPA1_LINEAS_HORIZONTALES.NOMBRE_CORTO, IPA1_LINEAS_HORIZONTALES.TIPO, IPA1_LINEAS_HORIZONTALES.FECHA_INI, IPA1_LINEAS_HORIZONTALES.FECHA_FIN, IPA1_LINEAS_HORIZONTALES.LINEA, IPA1_LINEAS_HORIZONTALES.SITUACION, IPA1_LINEAS_HORIZONTALES.UNIDADES, [IPA1].[Z]-[LINEA] AS COTA_LINEA
FROM IPA1 INNER JOIN IPA1_LINEAS_HORIZONTALES ON IPA1.COD = IPA1_LINEAS_HORIZONTALES.COD
WHERE IPA1_LINEAS_HORIZONTALES=?;
